package TAm;

public class Wrappers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String str = "Hello";
		char[] charArray = new char[str.length()];

		// Using a loop
		for (int i = 0; i < str.length(); i++) {
 charArray[i]= str.charAt(i);
		}

		
		

		System.out.println(charArray);
	}

				
}			
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
				
